import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { editAdminPassword, getAdmin } from '../../JS/Actions/admin';

const EditPassword = () => {

  const { _id } = useParams();
  const [newAdmin, setNewAdmin] = useState({});
  const adminToGet = useSelector((state) => state.adminReducer.adminToGet);
  const dispatch = useDispatch();
  const handleChange = (e) => {
    setNewAdmin({ ...newAdmin, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    dispatch(getAdmin(`${_id}`));
  });

  const edit = () => {
    dispatch(editAdminPassword(_id, newAdmin));
  };

  return (
    <div>
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Firstname</Form.Label>
          <Form.Control
            type="text"
            placeholder={`${adminToGet.firstname}`}
            name="firstname"
            value={newAdmin.firstname}
            onChange={handleChange}
          />

          <Form.Label>Name </Form.Label>
          <Form.Control
            type="text"
            placeholder={`${adminToGet.name}`}
            name="name"
            value={newAdmin.name}
            onChange={handleChange}
          />
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder={`${adminToGet.email}`}
            name="email"
            value={newAdmin.email}
            onChange={handleChange}
          />
        </Form.Group>
        <Link to={"/Profile"}>
          <Button variant="primary" type="submit" onClick={edit}>
            Submit
          </Button>
        </Link>
      </Form>
    </div>
  )
}

export default EditPassword
