import React from "react";
import "./Profile.css";
import { Button, Card } from "react-bootstrap";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import userico from "./user.png";
import edit from "./edit.png";

const Profile = () => {
  const user = useSelector((state) => state.userReducer.user);
  const admin = useSelector((state) => state.adminReducer.admin);
  const isAuth = useSelector((state) => state.userReducer.isAuth);
  const isAuthAdmin = useSelector((state) => state.adminReducer.isAuthAdmin);

  return (
    <div>
      <h2 id="pro">PROFILE</h2>
      {isAuth ? (
        <div className="Profile">
          <Card style={{ width: "16rem" }}>
            <Card.Img src={userico} className="userico" />
            <Card.Body>
              <h5>-Profile-</h5>
              <Card.Text>
                <span className="forms"> First Name : </span>
                {user && user.firstname}
              </Card.Text>
              <Card.Text>
                <span className="forms"> Last name : </span>
                {user && user.name}
              </Card.Text>
              <Card.Text>
                <span className="forms"> E-mail : </span>
                {user && user.email}
              </Card.Text>
              <Card.Text>
                <span className="forms"> Phone : </span>
                {user && user.phone}
              </Card.Text>
              <div className="btn">
                <Link to={`/EditInfos/${user._id}`}>
                  <img src={edit} className="editico" alt="edit" />
                </Link>
                <hr />
                <h5>Password</h5>
                <Link to={`/EditPassword/${user._id}`}>
                  <img src={edit} className="editico" alt="edit" />
                </Link>
              </div>
            </Card.Body>
          </Card>
        </div>
      ) : isAuthAdmin ? (
        <div className="Profile">
          <Card style={{ width: "16rem" }}>
            <Card.Img variant="top" src="holder.js/100px180" />
            <Card.Body>
            <h5>-Profile-</h5>
              <Card.Title>{admin && admin.firstname}</Card.Title>
              <Card.Title>{admin && admin.name}</Card.Title>
              <Card.Title>{admin && admin.email}</Card.Title>
              <div className="btn">
                <Link to={`/EditInfos/${admin._id}`}>
                  <Button className="btn1" variant="primary">
                    EDIT Infos
                  </Button>
                </Link>
                <Link to={`/EditPassword/${admin._id}`}>
                  <Button className="btn1" variant="primary">
                    EDIT Password
                  </Button>
                </Link>
              </div>
            </Card.Body>
          </Card>
        </div>
      ) : null}
    </div>
  );
};

export default Profile;
