import React, { useEffect } from 'react'
import { Button, Card } from 'react-bootstrap';
import { useDispatch } from 'react-redux';
import { currentAdmin } from '../../JS/Actions/admin';
import { deleteUser } from '../../JS/Actions/user';

const UserCard = ({userr}) => {
    const dispatch = useDispatch();
    const handleDelete = (e) => {
      e.preventDefault();
      dispatch(deleteUser(userr._id));
      window.location.reload();
    };
    useEffect(() => {
        if (localStorage.getItem("token")) {
          dispatch(currentAdmin());
        }
      }, [dispatch]);
  return (
    <div>
    <div className="orderCards">
      <Card>
        <Card.Body>
          <Card.Text>
            <span className="forms">firstname :</span>
            {userr.firstname}
          </Card.Text>
          <Card.Text>
            <span className="forms">name :</span>
            {userr.name}
          </Card.Text>
          <Card.Text>
            <span className="forms">email :</span>
            {userr.email}
          </Card.Text>
          <Card.Text>
            <span className="forms">Phone:</span>
            {userr.phone}
          </Card.Text>
          <Button variant="danger" onClick={handleDelete}>
            Delete user
          </Button>
        </Card.Body>
      </Card>
    </div>
  </div>
  )
}

export default UserCard
