import React, { useEffect, useState } from "react";
import { Button, Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import { editAdminInfos, getAdmin } from "../../JS/Actions/admin";

const EditInfos = () => {
  const { _id } = useParams();
  const [newAdmin, setNewAdmin] = useState({});
  const adminToGet = useSelector (state => state.adminReducer.adminToGet);
  const dispatch = useDispatch();
  const handleChange = (e) => {
    setNewAdmin({...newAdmin, [e.target.name] : e.target.value});
  };

  useEffect(() => {
    dispatch(getAdmin(`${_id}`))
     });
  
  const edit = () => {
    dispatch (editAdminInfos(_id, newAdmin));
  };
  return <div>
          <Form>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Name</Form.Label>
        <Form.Control 
            type="text" 
            placeholder={`${contactToGet.name}`} 
            name='name' 
            value={newContact.name} 
            onChange ={handleChange}
        />
        
        <Form.Label>Email </Form.Label>
        <Form.Control 
            type="email"
            placeholder= {`${contactToGet.email}`}
            name='email' 
            value={newContact.email} 
            onChange ={handleChange} />
        <Form.Label>phone</Form.Label>
        <Form.Control 
            type="number" 
            placeholder= {`${contactToGet.phone}`}
            name='phone' 
            value={newContact.phone} 
            onChange ={handleChange} />        
      </Form.Group>
      <Link to={'/Profile'}>
      <Button 
        variant="primary" 
        type="submit"
        onClick={edit}
        >
        Submit
      </Button>
      </Link>
    </Form>
  </div>;
};

export default EditInfos;
