import React, { useEffect, useState } from "react";
import { Button, Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link, useNavigate, useParams } from "react-router-dom";
import { editAdminPassword, getAdmin } from "../../JS/Actions/admin";

const EditPassword = () => {
  const { _id } = useParams();
  const navigate = useNavigate();
  const [newAdmin, setNewAdmin] = useState({});
  const admin = useSelector((state) => state.adminReducer.admin);
  const dispatch = useDispatch();
  const handleChange = (e) => {
    setNewAdmin({ ...newAdmin, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    dispatch(getAdmin(`${_id}`));
  });

  const edit = () => {
    dispatch(editAdminPassword(_id, newAdmin));
    navigate(`/Profile/${admin._id}`);
  };

  return (
    <div>
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>oldPassword</Form.Label>
          <Form.Control
            type="text"
            placeholder="Old Password"
            name="oldPassword"
            value={newAdmin.oldPassword}
            onChange={handleChange}
          />
          <Form.Label>password </Form.Label>
          <Form.Control
            type="text"
            placeholder=""
            name="name"
            value={newAdmin.password}
            onChange={handleChange}
          />
        </Form.Group>
        <Link to={"/Profile"}>
          <Button variant="primary" type="submit" onClick={edit}>
            Submit
          </Button>
        </Link>
      </Form>
    </div>
  );
};

export default EditPassword;
