import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import UserCard from "../../Components/UserCard/UserCard";
import { getUsers } from "../../JS/Actions/admin";


const UserList = () => {
  const listUsers = useSelector((state) => state.adminReducer.listUsers);
  const dispatch = useDispatch();
  const load = useSelector((state) => state.adminReducer.load);

  useEffect(() => {
    dispatch(getUsers());
  }, [dispatch]);

  return (
    <div className="alignthem">
      {load ? (
        <h2>loading...</h2>
      ) : (
        listUsers.map((el) => <UserCard userr={el} key={el._id} />)
      )}
    </div>
  );
};

export default UserList;
