import React, { useEffect, useState } from "react";
import {} from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Form, useNavigate, useParams } from "react-router-dom";
import { addOrder } from "../../JS/Actions/order";
import { getProduct } from "../../JS/Actions/product";

const AddOrder = () => {
  const { _id } = useParams();
  const productToGet = useSelector(
    (state) => state.productReducer.productToGet
  );
  const [newOrder, setNewOrder] = useState({
    productname: `${productToGet.name}`,
    email: "",
    adresse: "",
    phone: "",
  });
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const isAuth = useSelector((state) => state.userReducer.isAuth);

  useEffect(() => {
    dispatch(getProduct(`${_id}`));
  });
  const handleChange = (e) => {
    e.preventDefault();
    setNewOrder({ ...newOrder, [e.target.name]: e.target.value });
  };
  const addOrder = (e) => {
    e.preventDefault();
    dispatch(addOrder(_id, newOrder));
    navigate("/");
  };
  return (
    <div>
      <h1>send your order now</h1>
      <div>
        <img src={productToGet.image} alt="productImage" width="20%" />
        <h4>{productToGet.name}</h4>
        <h4>
          {productToGet.price} <span>DT</span>
        </h4>
      </div>
      <Form.Group className="mb-3">
        <Form.Label>Disabled input</Form.Label>
        <Form.Control placeholder="Disabled input" disabled />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Disabled select menu</Form.Label>
        <Form.Select disabled>
          <option>Disabled select</option>
        </Form.Select>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Check type="checkbox" label="Can't check this" disabled />
      </Form.Group>
    </div>
  );
};

export default AddOrder;
