import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom';
import { addOrder } from '../../JS/Actions/order';
import { getProduct } from '../../JS/Actions/product';

const AddOrder = () => {
    const { _id } = useParams();
    const productToGet = useSelector((state) => state.productReducer.productToGet);
    const [newOrder, setNewOrder] = useState({
        productname: `${productToGet.name}`,
        email: "",
        adresse: "",
        phone: "",
      });
      const navigate = useNavigate();
      const dispatch = useDispatch();
      const isAuth = useSelector((state) => state.userReducer.isAuth);
      const handleChange = (e) => {
        setNewOrder({ ...newOrder, [e.target.name]: e.target.value });
      };
      useEffect(() => {
        dispatch(getProduct(`${_id}`))
         });
      const add = () => {
        dispatch(addOrder(newOrder));
      };
  return (
    <div>AddOrder</div>
  )
}

export default AddOrder
