import React, { useEffect, useState } from "react";
import { Card, Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { addOrder } from "../../JS/Actions/order";
import { getProduct } from "../../JS/Actions/product";
import "./AddOrder.css";
import cart from "./cart.png";

const AddOrder = () => {
  const { _id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  
  const productToGet = useSelector(
    (state) => state.productReducer.productToGet
  );

  // Use another state variable to keep track of whether the product has been fetched
  const [isProductFetched, setIsProductFetched] = useState(false);

  const [newOrder, setNewOrder] = useState({
    productname: "",
    email: "",
    adress: "",
    phone: "",
  });
 
  const isAuth = useSelector((state) => state.userReducer.isAuth);

  useEffect(() => {
    dispatch(getProduct(`${_id}`));
  }, [_id, dispatch]);

  // This useEffect hook will run every time the productToGet variable changes
  useEffect(() => {
    if (productToGet && !isProductFetched) {
      setNewOrder({
        ...newOrder,
        productname: productToGet.name,
      });
      setIsProductFetched(true);
    }
  }, [productToGet, isProductFetched, newOrder]);

  const handleChange = (e) => {
    e.preventDefault();
    setNewOrder({ ...newOrder, [e.target.name]: e.target.value });
  };
  const add = (e) => {
    e.preventDefault();
    dispatch(addOrder( newOrder));
  };

  return (
    <div>
      <h2 id="SEND"> SEND YOUR ORDER NOW </h2>
      
      <div >
        <Card style={{ width: "16rem" }} className="addorder" >
          <Card.Img variant="top" src={productToGet.image} width="20%" />
          <Card.Body>
            <Card.Title>
              {productToGet.name} 
            </Card.Title>
            <Card.Title>
              {productToGet.price} <span>DT</span>
            </Card.Title>
            <Card.Text>{productToGet.description}</Card.Text>
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              name="email"
              onChange={handleChange}
              value={newOrder.email}
            />
            <Form.Label>Adress</Form.Label>
            <Form.Control
              type="text"
              name="adress"
              onChange={handleChange}
              value={newOrder.adress}
            />
            <Form.Label>Phone</Form.Label>
            <Form.Control
              type="text"
              name="phone"
              onChange={handleChange}
              value={newOrder.phone}
            />
            <hr />
            <div class="buy" onClick={isAuth ? add : navigate("/Login")}>
              Buy now <img src={cart} className="cart1" alt="img" width="15%" />
            </div>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
};

export default AddOrder;
