import React, { useEffect, useState } from "react";
import { Button, Card } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Form, useNavigate, useParams } from "react-router-dom";
import { addOrder } from "../../JS/Actions/order";
import { getProduct } from "../../JS/Actions/product";

const AddOrder = () => {
  const { _id } = useParams();
  const productToGet = useSelector(
    (state) => state.productReducer.productToGet
  );
  const [newOrder, setNewOrder] = useState({
    productname: `${productToGet.name}`,
    email: "",
    adresse: "",
    phone: "",
  });
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const isAuth = useSelector((state) => state.userReducer.isAuth);

  useEffect(() => {
    dispatch(getProduct(`${_id}`));
  });
  const handleChange = (e) => {
    e.preventDefault();
    setNewOrder({ ...newOrder, [e.target.name]: e.target.value });
  };
  const addOrder = (e) => {
    e.preventDefault();
    dispatch (addOrder(_id, newOrder));
    navigate("/");
  };
  return <div>
    <h1>send your order now</h1>
    <div>
    <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={productToGet.image} />
      <Card.Body>
        <Card.Title>{productToGet.name} </Card.Title>
        <Card.Title>{productToGet.price} <span>DT</span> </Card.Title>
<div>
<Form.Label>Email address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter email"
            name="email"
        
          />
</div>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>  
    </div>
  </div>;
};

export default AddOrder;
