import React, { useEffect, useState } from "react";
import { Button, Card, Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import {  useNavigate, useParams } from "react-router-dom";
import { getProduct } from "../../JS/Actions/product";

const AddOrder = () => {
  const { _id } = useParams();
  const productToGet = useSelector(
    (state) => state.productReducer.productToGet
  );
  const [newOrder, setNewOrder] = useState({
    productname: `${productToGet.name}`,
    email: "",
    adresse: "",
    phone: "",
  });
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const isAuth = useSelector((state) => state.userReducer.isAuth);

  useEffect(() => {
    dispatch(getProduct(`${_id}`));
  });
  const handleChange = (e) => {
    e.preventDefault();
    setNewOrder({ ...newOrder, [e.target.name]: e.target.value });
  };
  const addOrder = (e) => {
    e.preventDefault();
    dispatch(addOrder(_id, newOrder));
    navigate("/");
  };
  return (
    <div>
      <h1>send your order now</h1>
      <div className="Allcard">
        <div className="intro">
          <img src={productToGet.image} alt="productImage" width="20%" />
          <h4>{productToGet.name}</h4>
          <h4>
            {productToGet.price} <span>DT</span>
          </h4>
    
          <div>
          <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={productToGet.image} width="20%" />
      <Card.Body>
      <Card.Title>{productToGet.name} <span>DT</span></Card.Title>
        <Card.Title>{productToGet.price} <span>DT</span></Card.Title>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
        <Form.Control
              type="email"
              name="email"
              onChange={handleChange}
              value={newOrder.email}
            />
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>
    </div>
        </div>
      </div>
    </div>
  );
};

export default AddOrder;
