import React, { useEffect } from "react";
import { Navbar, Container, Nav, Form, Button } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { currentAdmin, logoutAdmin } from "../../JS/Actions/admin";
import { current, logout } from "../../JS/Actions/user";
import "./NavBar.css";
import logo from "./VERSTAND.png";
const NavBar = () => {

  const dispatch = useDispatch();
  useEffect(() => {
    if (localStorage.getItem("token")) {
      dispatch(current());
    }
  }, [dispatch]);

  useEffect(() => {
    if (localStorage.getItem("token")) {
      dispatch(currentAdmin());
    }
  }, [dispatch]);

  const navigate = useNavigate();
  const isAuth = useSelector((state) => state.userReducer.isAuth);
  const isAuthAdmin = useSelector((state) => state.adminReducer.isAuthAdmin);
  const admin = useSelector((state) => state.adminReducer.admin);
 
  return (
    
    <header>
  
      <Navbar bg="light" variant="light" className="NavBar">
        <Container>
          <Navbar.Brand href="/">
            <img
              alt="Verstand"
              src={logo}
              width="40%"
              height="10%"
              className="d-inline-block align-top"
            />{" "}
            
          </Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="/">Home</Nav.Link>
            <Nav.Link href="/AboutUs">About us</Nav.Link>
            <Nav.Link href="/ContactUs">Contact us</Nav.Link>
            
            <Form className="d-flex">
                  <Form.Control
                    type="search"
                    placeholder="Search"
                    className="me-2"
                    aria-label="Search"
                  />
                  <Button variant="outline-success">Search</Button>
                </Form>

            {isAuth ? (<div>
              <Nav.Link href="/">
                <button onClick={() => dispatch(logout())}>Log out</button>
                </Nav.Link>
                <Nav.Link href="/Profile/:_id">Profile</Nav.Link>
                </div>
            ) : isAuthAdmin ? (
              <Nav.Link href="/">
                <button onClick={() => dispatch(logoutAdmin())}>Log out</button>
                <button onClick={() => navigate(`/Profile/${admin._id}`)}>Profile</button>
              </Nav.Link>
            ) : (
              <div className="LogRegis">
                <Nav.Link href="/Login">Login</Nav.Link>
                <Nav.Link href="/Register">Register</Nav.Link>
              </div>
            )}
          </Nav>
        </Container>
      </Navbar>
    
    </header>
  );
};

export default NavBar;
